#code for generating bill
print("BILL DETAILS")                                                             #BILL DETAILS
pizza=int(input("enter the no of pizza brought:"))                                #enter the no of pizza brought:2
puffs=int(input("enter the no of puffs brought:"))                                #enter the no of puffs brought:3
colddrinks=int(input("enter the no of colddrinks brought:"))                      #enter the no of colddrinks brought:4
print("total price of pizza is",100*pizza)                                        #total price of pizza is 200
print("total price of puffs is",20*puffs)                                         #total price of puffs is 60
print("total price of colddrinks is",10*colddrinks)                               #total price of colddrinks is 40
totalprice=100*pizza+20*puffs+10*colddrinks                                       #total price=100*2+20*3+10*4=300
print("total price is",totalprice)                                                #total price is 300
